    work_address: Optional[RootWorkAddress] = Field(
        None,
        description='<!DOCTYPE html>\n<html lang="en">\n  <head>\n    <meta charset="UTF-8" />\n    <title>Schengen Visa Application Guidelines</title>\n    <style>\n      .container {\n        background-color: #fefefe;\n        padding: 16px;\n        display: flex;\n        flex-direction: column;\n        justify-content: center;\n        font-family: "Inter", sans-serif;\n        color: #111;\n      }\n      .container.schengen {\n        /* width: 540px; */\n        max-width: 100%;\n      }\n      .container p,\n      .container ul,\n      .container li {\n        font-weight: 400;\n        font-size: 16px;\n        line-height: 34px;\n        letter-spacing: 0;\n        color: #333;\n        margin: 0;\n      }\n      .container p {\n        margin-bottom: 16px;\n      }\n      .container ul {\n        padding-left: 20px;\n        margin-bottom: 16px;\n      }\n      .container li {\n        margin-bottom: 8px;\n      }\n      .container .heading {\n        font-weight: 400;\n        font-size: 18px;\n        line-height: 34px;\n        letter-spacing: 0;\n        margin-bottom: 8px;\n      }\n      .container .subheading {\n        font-weight: 400;\n        font-size: 18px;\n        line-height: 34px;\n        letter-spacing: 0;\n        margin-bottom: 16px;\n      }\n      .container .note {\n        font-weight: 400;\n        font-size: 16px;\n        line-height: 34px;\n        letter-spacing: 0;\n        color: #555;\n        margin: 0;\n      }\n    </style>\n  </head>\n  <body>\n    <div class="container schengen">\n      <!-- <div class="heading" style="font-size: 30px; font-weight: 600; margin-bottom: 8px;">Schengen Visa Application Guidelines</div> -->\n\n      <p style="font-size: 22px; font-weight: 700; margin-bottom: 16px">\n        <!-- Follow these steps carefully to ensure smooth processing of your Schengen visa application: -->\n        Need help with this part? Here’s what you need to know about each field\n        in this section.\n      </p>\n\n      <ul>\n        <li>\n          Please provide your details — for example, whether you are an employee\n          or a student. This will help the TTK Visa Consultant advise you on the\n          specific documentation required and provide the necessary support.\n        </li>\n        <li>\n          If you are unemployed or not currently working, you may skip this\n          section by selecting “N/A” or “Not Applicable”.\n        </li>\n      </ul>\n\n      <!-- <p class="subheading"><strong>Important:</strong></p>\n    <ul>\n      <li>All documents must be printed on A4 size sheets only.</li>\n      <li>All applicants must book an appointment online to submit their visa application and biometric data at the Schengen visa application centre.</li>\n      <li>Required documents must be submitted with the visa application (no fax or email to Embassy accepted).</li>\n      <li>Only documents listed in the checklist will be accepted.</li>\n      <li>If your visa has been refused previously, you must submit the refusal notice or a written explanation of the reason for travel and refusal.</li>\n    </ul>\n\n    <p class="subheading"><strong>Brief Procedure:</strong></p>\n    <ul>\n      <li>TTK will review your documents and suggest necessary changes before submission.</li>\n      <li>TTK will book an appointment as per jurisdiction at the Visa Application Centre (VAC), subject to slot availability. Inform TTK of your tentative dates at the earliest.</li>\n      <li>Applicants must visit the VAC in person to provide biometric fingerprint data with a signed application form, appointment letter, and checklist documents.</li>\n      <li>Alternatively, TTK can submit the application if the applicant qualifies for biometric waiver due to previous Schengen visa issuance.</li>\n      <li>Courier service is available by filling out the \'Courier Request Form\'. The passport will be sent to the address mentioned in the form.</li>\n    </ul>\n\n    <p class="subheading"><strong>Processing Time:</strong></p>\n    <ul>\n      <li>Standard processing time: 10 to 15 business days from the date of application at the VFS Application Centre.</li>\n      <li>Processing duration may vary. Final decision is at the discretion of the Embassy/Consulate.</li>\n      <li>Consular officers may request additional documents or a personal interview if required.</li>\n    </ul> -->\n\n      <p class="note">\n        <!-- <strong>Disclaimer:</strong> TTK Services Private Limited ("TTK") will facilitate services in compliance with the respective foreign mission\'s requirements. TTK is not involved in the adjudication of visa applications or decisions related to approval/refusal. TTK shall not be held responsible for delays, restricted visa entries, or further documentation/interview requests made by the consular authority. -->\n        <!-- (To be provided by TTK) -->\n      </p>\n    </div>\n  </body>\n</html>\n',
        title='Work/Education',
    )
