    accomodation: Optional[RootAccomodation] = Field(
        None,
        description='<!DOCTYPE html>\n<html lang="en">\n  <head>\n    <meta charset="UTF-8" />\n    <title>Guidelines for Accommodation</title>\n    <style>\n      /* Container uses Inter for everything */\n      .container {\n        /* border-radius: 6px; */\n        background-color: #fefefe;\n        /* box-sizing: border-box; */\n        padding: 16px;\n        display: flex;\n        flex-direction: column;\n        justify-content: center;\n        font-family: "Inter", sans-serif;\n        color: #111;\n      }\n\n      /* Regular form text */\n      .container p,\n      .container ul,\n      .container li {\n        font-weight: 400;\n        font-size: 16px;\n        line-height: 34px;\n        letter-spacing: 0;\n        color: #333;\n        margin: 0;\n      }\n\n      .container p {\n        margin-bottom: 16px;\n      }\n\n      .container ul {\n        padding-left: 20px;\n        margin-bottom: 16px;\n      }\n\n      .container li {\n        margin-bottom: 8px;\n      }\n\n      .container li.highlight {\n        /* inherits the same color as body text */\n        color: inherit;\n      }\n\n      /* Emphasized text (strong, headings) */\n      .container strong,\n      .container .heading {\n        font-weight: 400; /* keep same weight */\n        font-size: 18px; /* slightly smaller for emphasis */\n        line-height: 34px;\n        letter-spacing: 0;\n      }\n    </style>\n  </head>\n  <body>\n    <div class="container">\n      <!-- Use a helper class for headings -->\n      <!-- <div class="heading" style="font-size: 30px; font-weight: 600; margin-bottom: 8px;">\n      Guidelines for Accommodation\n    </div> -->\n\n      <div\n        class="heading"\n        style="font-size: 22px; font-weight: 700; margin-bottom: 16px"\n      >\n        <!-- Need help with this part? Here’s what you need to know. -->\n        Need help with this part? Here’s what you need to know about each field\n        in this section.\n      </div>\n\n      <ul>\n        <li>\n          Please provide accurate and complete details of your accommodation\n          details, including, country of travel and duration of stay.\n        </li>\n        <li>\n          This information is critical for validating your travel plans as part\n          of the visa application documentation.\n        </li>\n        <li>\n          Ensure it aligns with your itinerary and tickets to avoid\n          inconsistencies that could delay processing.\n        </li>\n      </ul>\n\n      <!-- <ul>\n      <li>\n        <strong>Submit Confirmed Bookings:</strong>\n        Upload hotel reservations, Airbnb confirmations, or any other proof of stay for the entire duration of your trip.\n      </li>\n      <li>\n        <strong>Cover All Destinations:</strong>\n        If you’re visiting multiple cities or countries, make sure you include accommodation details for each stop.\n      </li>\n      <li>\n        <strong>Match with Your Itinerary:</strong>\n        The check-in/check-out dates should align with your travel itinerary and transportation bookings.\n      </li>\n      <li>\n        <strong>Invitation:</strong>\n        If you\'re staying with friends or family, upload a signed Invitation Letter from your host, along with a copy of their ID or proof of residence.\n      </li>\n      <li class="highlight">\n        <strong>One Document or Multiple:</strong>\n        You can upload a single file with all your accommodation details, or multiple files, whatever is easier for you.\n      </li>\n      <li>\n        <strong>Don’t have confirmed bookings yet?</strong>\n        We can help create a sample plan for you, just choose our Add-On Service.\n      </li>\n    </ul> -->\n    </div>\n  </body>\n</html>\n',
        title='Accommodation',
    )
